// GoogleMap
google.load("maps", "3.x", {"other_params":"sensor=false"});

//マップの初期設定
function initialize() {

    //マップの中心座標
    var myLatLng = new google.maps.LatLng(35.6627496,139.7089196);

    //マップの設定オプション
    var myOptions = {
        zoom: 15,                                 //ズームレベル
        center: myLatLng,                         //中心座標
        mapTypeId: google.maps.MapTypeId.ROADMAP,  //マップタイプ
        mapTypeControl: false,
        streetViewControl: false,
        disableDefaultUI: true,
        disableDoubleClickZoom: true,
        draggable: false,
        keyboardShortcuts: false,
        scrollwheel: false,
        mapTypeControlOptions: {
            mapTypeIds: [google.maps.MapTypeId.ROADMAP, 'simple']
        }
    };

    map = new google.maps.Map(document.getElementById("map"), myOptions);

    var marker = new google.maps.Marker({
        position: myLatLng,
        map: map
    });

    var samplestyle = [
        {
            featureType: "all",
            elementType: "all",
            stylers: [
                { visibility: "on" },
                { saturation: -96 }
            ]
        }
    ];

    var samplestyleOptions = {
        name: "シンプル"
    };

    var sampleMapType = new google.maps.StyledMapType(samplestyle, samplestyleOptions);
    map.mapTypes.set('simple', sampleMapType);
    map.setMapTypeId('simple');
}

$(function() {

    if( $('#map') ){
        google.setOnLoadCallback(initialize);
    }

    // block2の高さを揃える
    $(window).load(function() {
        google.setOnLoadCallback(initialize);
        $(".block2__body__col__head").tile(3);
    });

    // スマホ時のグローバルナビ
    (function($) {
        var module = $('.header');
        var btnToggle = module.find('.header__btnToggle')
        var toggleBody = module.find('.header__toggleBody')
        var spd = 300;

        btnToggle.click(function(){
            if(toggleBody.css('display')=='none'){
                toggleBody.slideDown(spd);
            }else {
                toggleBody.slideUp(spd);
            }
        });
    })(jQuery);

});